import React from 'react';
import {data} from "./store/store.jsx"
import Product from "./components/productcart/Product"
import logo from './logo.svg';
import './App.css';

// при удалении дтовара что бы пересчитыывалась карта
// вывод название товара, так же удаление
// подсчет общей сумы
// пагинация
class App extends React.Component {
    constructor() {
        super();

        this.state = {
            qty: data,
            productToWillBuy: []
        };
    };

    removeElement = el => {
        const newList = this.state.qty.filter(function (item) {
            //console.log(item.id);
            return item.id !== el.id;

        });

        const newListAfterDelete = this.state.productToWillBuy.filter(function (item) {
            return item.id !== el.id
        })

        console.log(newListAfterDelete)

        this.setState({
            productToWillBuy: newListAfterDelete
        });


        console.log(newList)
        this.setState({
            qty: newList
        });
    };

    productToWillBuy = el => {
        const updateElement = [... this.state.productToWillBuy];
        updateElement.push(el)


        let ListProd  = updateElement.map(function (item) {
            return item.title
        })
        console.log(ListProd)

        this.setState({
            productToWillBuy: updateElement
        })
    };


    removeElementFromBuy = el => {
        const updataNewList = this.state.productToWillBuy.filter(function (item) {
            return item.id !== el.id;
        });

        this.setState({
            productToWillBuy: updataNewList
        });
    };

    render() {
        return (
            <div className="mainContainer">d
                <div className="products">
                    {this.state.qty.map(el => {
                        return (
                            <div>
                                <Product
                                    key={el.id}
                                    el={el}
                                    removeElement={this.removeElement}
                                    productToWillBuy={this.productToWillBuy}
                                    removeElementFromBuy={this.removeElementFromBuy}/>
                            </div>
                        )
                    })}
                </div>

                <div className="result-container">
                    <div className="result-container-item">
                        Will Buy: {this.state.productToWillBuy.length}
                        <div>{this.state.productToWillBuy.title}</div>
                            <div>{ListProd}</div>
                    </div>
                </div>

            </div>
        )
    }
}


export default App;
