import React from 'react';
import {data} from "./store/store.jsx"
import Product from "./components/productcart/Product"
import logo from './logo.svg';
import './App.css';


class App extends React.Component {
    constructor() {
        super();

        this.state = {
            qty: data,
            productToWillBuy: []
        };
    };

    removeElement = el => {
        const newList = this.state.qty.filter(function (item) {
            return item.id !== el.id;
        });
        this.setState({
            qty: newList
        });
    };

    productToWillBuy = el => {
        const updateElement = [... this.state.productToWillBuy];
        updateElement.push(el)

        this.setState({
            productToWillBuy: updateElement
        })
    };

    removeElementFromBuy = el => {
        const newList = this.state.qty.filter(function (item) {
            return item.id !== el.id;
        });

        this.setState({
            qty: newList
        });
    };

    render() {
        return (
            <div className="mainContainer">
                <div className="products">
                    {this.state.qty.map(el => {
                        return (
                            <div>
                                <Product
                                    key={el.id}
                                    el={el}
                                    removeElement={this.removeElement}
                                    productToWillBuy={this.productToWillBuy}/>
                            </div>
                        )
                    })}
                </div>

                <div className="result-container">
                    <div className="result-container-item">
                        Will Buy: {this.state.productToWillBuy.length}
                    </div>
                </div>

            </div>
        )
    }
}


export default App;
